    return gulp.src(['app/js/index.js'])
        .pipe(plumber({
            errorHandle: function() {

            }
        }))
        .pipe(named())
        .pipe(gulpWebpack({
            mode: 'development',
            module:{
                rules:[{
                    test:/\.js$/,
                    exclude: /(node_modules|bower_components)/,
